mapper.select:
		select recruit_no, title, field, location, age,recruitingStart as checkDate,volunteering_start_time,volunteering_end_time,recruiting_end,volunteering_start_date,volunteering_end_date
		from(select recruit_no, title, field,location, age,volunteering_start_time, volunteering_end_time,recruiting_start as recruitingStart,recruiting_end,volunteering_start_date,volunteering_end_date from recruit order by recruit_no desc)
		
			
				AND field=#{field}
			
			
				AND location=#{location}
			
			
  			
   			 AND volunteering_start_time>=#{volunteeringStartTime} 
    		 
			
			
  			
   			 AND volunteering_end_time<=#{volunteeringEndTime}

		select recruit_no, title, field, location, age,volunteering_start_date,volunteering_end_date,recruiting_end as recruitingEnd
		from(select recruit_no, title, field, location, age, volunteering_start_date,volunteering_end_date,recruiting_end,
		ceil(rownum/5) as page from(select recruit_no, title, field,	location, age,volunteering_start_date, volunteering_end_date
		,recruiting_end from recruit
		
			
				AND field=#{field}
			
			
				AND location=#{location}
			
			
  			
   			 AND volunteering_start_date>=#{volunteeringStartDate} 
    		 
			
			
  			
   			 AND volunteering_end_date<=#{volunteeringEndDate}
    		 
			
			
			AND title like '%${title}%'
			
		
		order by recruit_no desc) )
		where page=#{pageNo}

		select count(*) from recruit
		
			
				AND field=#{field}
			
			
				AND location=#{location}
			
			
  			
   			 AND volunteering_start_date>=#{volunteeringStartDate} 
    		 
			
			
  			
   			 AND volunteering_end_date<=#{volunteeringEndDate}
    		 
			
			
			AND title like '%${title}%'

		select distinct(recruiting_start) as date_list from (
		select volunteering_start_time,volunteering_end_time,recruiting_start,field,location from recruit)
		
			
				AND field=#{field}
			
			
				AND location=#{location}
			
			
  			
   			 AND volunteering_start_time>=#{volunteeringStartTime} 
    		 
			
			
  			
   			 AND volunteering_end_time<=#{volunteeringEndTime}
    		 
			
		
		order by date_list asc
